function convertToRoman(num) {
 // mathisfun lists decimal values for roman numerals
 // It was difficult to figure out which ones would go into these lists. It was trial and error until I figured out which ones would allow me to subtract at each "decimal". Roman numerals don't stop at "point zero"
 let romeArr = ['I', 'IV', 'V', 'IX', 
 'X', 'XL', 'L', 'XC', 
 'C', 'CD', 'D', 'CM', 'M'];
 let decArr = [1, 4, 5, 9, 
 10, 40, 50, 90, 
 100, 400, 500, 900, 1000];

 var romeNum = ''; // we are going to add the corresponding strings from romeArr to this

 var romanNumerals = romeArr.reverse();
 var integers = decArr.reverse();// we can split the num into an array because there are 3 Roman Numerals maximum in result

 // loop iterates to search for corresponding roman numeral that matches the num
 for (var i = 0; i < integers.length; i++) {
     while (integers[i] <= num) {
         romeNum += romanNumerals[i] // After Larger is Added
         num -= integers[i];
     }

    }

 console.log(romeNum);
 return romeNum;
}

convertToRoman(36);
