function telephoneCheck(str) {
  // best to use regex to implement this program
  console.log("Input: " + str);
  let numOnly = /^(1\s?)?(\(\d{3}\)|\d{3})[\s\-]?\d{3}[\s\-]?\d{4}$/;
  // numOnly allows for a 1 or not to exist.
  // there can be sets of 3 digits
  // the "d{3}" has parentheses OR not
  // check for white spaces or dashes with the next one
  // the $ states the end of the string is 4 digits.
  // we will use this numOnly as a reGex for a test.
  let phoneNumber = str;

  console.log(phoneNumber);

  return numOnly.test(phoneNumber);
}

telephoneCheck("555-555-5555");
