function Cat(name) {
  this.name = name; 
}
// Don't Repeat Yourself (DRY)
Cat.prototype = {
  constructor: Cat, 
  // will inherit properties of Animal.prototype
};

function Bear(name) {
  this.name = name; 
}

Bear.prototype = {
  constructor: Bear, 
  // will inherit properties of Animal.prototype
};

function Animal() { }

Animal.prototype = { // super constructor
  constructor: Animal,
   eat: function() {
    console.log("nom nom nom");
  } 
};
