function chunkArrayInGroups(arr, size) {
  // Break it up.
  var tempArr = [];
  var newArr = [];

  for (var i = 0; i < arr.length; i++) { // loops the entire arr

    if (i % size !== size - 1) {
      tempArr.push(arr[i]);
    } else {
      tempArr.push(arr[i]);
      newArr.push(tempArr);
      tempArr = [] // clear this temporary array once it is a subArray
    }
  }

  if (tempArr.length !== 0) {
    newArr.push(tempArr); // it pushes if it isn't empty
    }
    return newArr; // return the array
}

chunkArrayInGroups(["a", "b", "c", "d"], 2);
