function findLongestWordLength(str) {
  let wordArr = str.split(" ");


  let lastLength = 0
  let longestWord

  for (let i = 0; i < wordArr.length; i++) {
    if (wordArr[i].length > lastLength) {
      lastLength = wordArr[i].length // set it to the new length
    longestWord = wordArr[i]; // set element to longest word  
    }
  }
  return lastLength;
}

