function mutation(arr) {
  var secondChars = arr[1].toLowerCase(); // return new array with lowercase characters
  var firstChars = arr[0].toLowerCase(); // return new array with lowercase characters

  for (var i = 0; i < secondChars.length; i++) {
    // we use the length of the second word as a test comparison
    if (firstChars.indexOf(secondChars[i]) < 0) {
      return false; // returns false if sentinel value
    } 

  } 

  return true; // 0 or more matching letters
}

mutation(["hello", "hey"]);
