function titleCase(str) {
 var wordArr = str.toLowerCase().split(" "); // split each word into elements of an array

  for (let i = 0; i < wordArr.length; i++) {
    var char = wordArr[i].split("");
    char[0] = char[0].toUpperCase(); // REASSIGN uppercase
    wordArr[i] = char.join(""); // join the subarray
  }

  var newString = wordArr.join(" "); // rejoin as string with spaces between each word
  return newString;
}

